const path = require('path');
const fs = require('fs');

// Determine the library directory relative to this file.
const libDir = path.join(path.dirname(fs.realpathSync(__filename)), '../lib');

// Import the simple and advanced math modules.
const simpleMath = require(path.join(libDir, 'simple_math.js'));
const advancedMath = require(path.join(libDir, 'advance_math.js'));

module.exports = {
  addition: simpleMath.addition,
  subtraction: simpleMath.subtraction, // Fixed typo: "subtractio" â†’ "subtraction"
  multiplication: advancedMath.multiply,
  division: advancedMath.divide,
  fibo: advancedMath.fibo
};
